select.if:
SELECT * FROM (SELECT rowNum rn, a.* FROM (SELECT b.book_no, book_title, book_kind, book_author, book_publisher, book_publish_date, sum(deal_count) deal_count, sum(deal_price) deal_price FROM book b, deal d WHERE b.book_no=d.book_no AND used_no is null AND deal_date >= TO_CHAR(#{dateFrom}, 'YYYYMMDD') AND deal_date < TO_CHAR(#{dateTo}+1, 'YYYYMMDD')
AND book_kind=#{book_kind}
GROUP BY b.book_no, book_title, book_kind, book_author, book_publisher, book_publish_date ORDER BY deal_count DESC)a) WHERE rn BETWEEN #{startRow} and #{endRow}

SELECT * FROM (SELECT rowNum rn, a.* FROM (SELECT b.book_no, book_title, book_kind, book_author, book_publisher, book_publish_date, sum(deal_count) deal_count, sum(deal_price) deal_price FROM book b, deal d WHERE b.book_no=d.book_no AND used_no is null AND deal_date >= TO_CHAR(#{dateFrom}, 'YYYYMMDD') AND deal_date < TO_CHAR(#{dateTo}+1, 'YYYYMMDD')
AND book_kind=#{book_kind}
GROUP BY b.book_no, book_title, book_kind, book_author, book_publisher, book_publish_date ORDER BY deal_price DESC)a) WHERE rn BETWEEN #{startRow} and #{endRow}
